mapper.select:
SELECT * FROM  content

     created_time = #{createdTime}
     AND title = #{title}
     AND description = #{description}
     AND type = #{type}
     AND user_id = #{userId}
     AND images = #{images}
     AND del = #{del}

LIMIT #{offset},#{size}

SELECT * FROM  content

     created_time = #{createdTime}
     AND title = #{title}
     AND description = #{description}
     AND type = #{type}
     AND user_id = #{userId}
     AND images = #{images}
     AND del = #{del}

SELECT count(*) FROM  content

     created_time = #{createdTime}
     AND title = #{title}
     AND description = #{description}
     AND type = #{type}
     AND user_id = #{userId}
     AND images = #{images}
     AND del = #{del}

mapper.delete:
DELETE FROM content

     created_time = #{createdTime}
     AND title = #{title}
     AND description = #{description}
     AND type = #{type}
     AND user_id = #{userId}
     AND images = #{images}
     AND del = #{del}

mapper.update:
UPDATE content

      AND title = #{title}
      AND description = #{description}
      AND type = #{type}
      AND user_id = #{userId}
      AND images = #{images}
      AND del = #{del}

WHERE id = #{id};
