     AND name = #{name}
     AND description = #{description}
     AND expect_time = #{expectTime}
     AND finished_time = #{finishedTime}
     AND type = #{type}
     AND user_id = #{userId}
     AND is_public = #{isPublic}
     AND images = #{images}
     AND remind = #{remind}

mapper.update:
UPDATE lemon

      created_time = #{createdTime}
      AND finished = #{finished}
      AND name = #{name}
      AND description = #{description}
      AND expect_time = #{expectTime}
      AND finished_time = #{finishedTime}
      AND type = #{type}
      AND user_id = #{userId}
      AND is_public = #{isPublic}
      AND images = #{images}
      AND remind = #{remind}

WHERE id = #{id};
