mapper.select:
SELECT * FROM  lemon

     created_time = #{createdTime}
     AND finished = #{finished}
     AND name = #{name}
     AND description = #{description}
     AND expect_time = #{expectTime}
     AND finished_time = #{finishedTime}
     AND level = #{level}
     AND user_id = #{userId}
     AND is_public = #{isPublic}

SELECT * FROM  lemon

     created_time = #{createdTime}
     AND finished = #{finished}
     AND name = #{name}
     AND description = #{description}
     AND expect_time = #{expectTime}
     AND finished_time = #{finishedTime}
     AND level = #{level}
     AND user_id = #{userId}
     AND is_public = #{isPublic}

SELECT count(*) FROM  lemon

     created_time = #{createdTime}
     AND finished = #{finished}
     AND name = #{name}
     AND description = #{description}
     AND expect_time = #{expectTime}
     AND finished_time = #{finishedTime}
     AND level = #{level}
     AND user_id = #{userId}
     AND is_public = #{isPublic}

mapper.delete:
DELETE FROM lemon

     created_time = #{createdTime}
     AND finished = #{finished}
     AND name = #{name}
     AND description = #{description}
     AND expect_time = #{expectTime}
     AND finished_time = #{finishedTime}
     AND level = #{level}
     AND user_id = #{userId}
     AND is_public = #{isPublic}

mapper.update:
UPDATE lemon

     finished = #{finished},
     name = #{name},
     description = #{description},
     expect_time = #{expectTime},
     finished_time = #{finishedTime},
     level = #{level}
      AND is_public = #{isPublic}

WHERE id = #{id};
