mapper.delete:
DELETE FROM u_user

    avatar = #{avatar}
    and gender = #{gender}
    and name = #{name}
    and nick_name = #{nickName}
    and birthday = #{birthday}
    and account = #{account}
    and email = #{email}
    and password = #{password}
    and salt = #{salt}
    and status = #{status}
    and score = #{score}

mapper.update:
UPDATE u_user

    avatar = #{avatar}
    , gender = #{gender}
    , name = #{name}
    , nick_name = #{nickName}
    , birthday = #{birthday}
    , account = #{account}
    , email = #{email}
    , password = #{password}
    , salt = #{salt}
    , status = #{status}
    , profile = #{profile}
    , modify_available = #{modifyAvailable}

WHERE id = #{id};

