mapper.delete:
DELETE FROM u_user

    avatar = #{avatar}
    and gender = #{gender}
    and name = #{name}
    and nick_name = #{nickName}
    and birthday = #{birthday}
    and account = #{account}
    and email = #{email}
    and password = #{password}
    and salt = #{salt}
    and status = #{status}
    and type = #{type}
    and score = #{score}
    and zone_status = #{zoneStatus}

mapper.update:
UPDATE u_user

    avatar = #{avatar}
    and gender = #{gender}
    and name = #{name}
    and nick_name = #{nickName}
    and birthday = #{birthday}
    and account = #{account}
    and email = #{email}
    and password = #{password}
    and salt = #{salt}
    and status = #{status}
    and type = #{type}
    and score = #{score}
    and zone_status = #{zoneStatus}
    and profile = #{profile}
    and sms_count = #{smsCount}
    and modify_available = #{modifyAvailable}

WHERE id = #{id};

